import React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ChildCareIcon from '@mui/icons-material/ChildCare';
import BedroomBabyIcon from '@mui/icons-material/BedroomBaby';
import BugReportIcon from '@mui/icons-material/BugReport';
import {
    NavLink as RouterLink,
} from 'react-router-dom';
import {ListItemButton} from "@mui/material";
import {useLocation} from "react-router-dom"

import './Drawer.scss'

const menuItems = [
    {
        desc: 'List',
        icon: <ChildCareIcon color="primary"/>,
        path: '/children',
    },
    {
        desc: 'Gifts',
        icon: <BedroomBabyIcon color="primary"/>,
        path: '/gifts',
    },
    {
        desc: 'Test',
        icon: <BugReportIcon color="primary"/>,
        path: '/test',
    }
];

export const PermanentDrawer = ({width}) => {
    const drawerWidth = width;
    const location = useLocation();
    const { window } = props;
    const [mobileOpen, setMobileOpen] = React.useState(false);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    };


    const CustomListItem = ({to, primary, children}) => (
        <ListItemButton
            component={RouterLink}
            to={to}
            selected={to === location.pathname}
        >
            {children}
            <ListItemText primary={primary} sx={{paddingLeft: 2}}></ListItemText>
        </ListItemButton>
    )

    return (
        <Box sx={{display: 'flex'}}>
            <CssBaseline/>
            <Drawer
                sx={{
                    width: drawerWidth,
                    flexShrink: 0,
                    '& .MuiDrawer-paper': {
                        width: drawerWidth,
                        boxSizing: 'border-box',
                    },
                }}
                variant="permanent"
                anchor="left"
            >
                <Toolbar/>
                <Divider/>
                <List>
                    {menuItems.map((item, index) => (
                        <CustomListItem to={item.path} key={item.desc} primary={item.desc}>
                            {item.icon}
                        </CustomListItem>
                    ))}
                </List>
                <Divider/>
            </Drawer>

        </Box>
    );
}
